import React, { useEffect, useState } from "react";
import Card from "../components/card.js";
import Welcome from "../components/welcome.js";

const AllData = () => {
  // state for users array
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // fetch user accounts from API
    fetch(`/account/find`, {
      headers: {
        "x-access-token": localStorage.getItem("token"),
      },
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setUsers(data);
      });
  }, []);

  let list = users.map((user, index) => {
    return (
      <tr key={index}>
        <td className="fs-6 text-wrap">{user.name}</td>
        <td className="fs-6 text-wrap">{user.email}</td>
        <td className="fs-6 text-wrap">${user.balance}</td>
      </tr>
    );
  });

  return (
    <>
      <Welcome />
      <Card
        className="withdrawal-page p-3"
        hdrcolor="greenyellow"
        hdrtext="#282c34"
        bodycolor="dodgerblue"
        bodytext="#282c34"
        header="All Data"
        body={
          <table className="table">
            <thead>
              <tr>
                <th className="fs-6" scope="col">
                  Name
                </th>
                <th className="fs-6" scope="col">
                  Email
                </th>
                <th className="fs-6" scope="col">
                  Balance
                </th>
              </tr>
            </thead>
            <tbody>{list}</tbody>
          </table>
        }
      ></Card>
    </>
  );
};
export default AllData;
