import { useState } from "react";
import Card from "../components/card.js";
import { useNavigate } from "react-router-dom";

const Login = () => {
  //shows input fields and hides them after from submitted
  const [show, setShow] = useState(true);
  //sets error messsage
  const [status, setStatus] = useState("");

  return (
    <Card
      hdrcolor="greenyellow"
      hdrtext="#282c34"
      bodycolor="dodgerblue"
      bodytext="#282c34"
      header="Login"
      status={status}
      body={
        show ? (
          <LoginForm setShow={setShow} setStatus={setStatus} />
        ) : (
          <LoginSuccess setShow={setShow} setStatus={setStatus} />
        )
      }
    />
  );
};
function LoginSuccess(props) {
  return (
    <>
      <h5>Success</h5>
      <button
        type="submit"
        className="btn btn-light"
        onClick={() => props.setShow(true)}
      >
        Logout
      </button>
    </>
  );
}
const LoginForm = (props) => {
  //sets user properties
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  //for enabling submit button
  const [validTransaction, setValidTransaction] = useState(false);
   //for redirect after login
  const navigate = useNavigate();
  const handleLogin = () => {

    if (!validate(email, "email")) return;
    if (!validate(password, "password")) return;
    if (password.length < 8) {
      alert("Password Requires a minimum of 8 Characters!");
      return;
    }
    fetch(`/account/login/${email}/${password}`)
      .then((response) => response.text())
      .then((text) => {
        try {
          const data = JSON.parse(text);
          localStorage.setItem("token1", data.email);
          localStorage.setItem("token2", data.balance);
          localStorage.setItem("token3", data.name);
          const token1 = localStorage.getItem("token1");
          const token2 = localStorage.getItem("token2");
          const token3 = localStorage.getItem("token2");
          props.setStatus("");
          props.setShow(false);
          console.log("JSON:", data);

          alert(`Login Successful, Welcome back ${data.name}!`);
          props.setShow(false);
          clearForm();
          navigate("/");
        } catch (err) {
          props.setStatus(text);
          console.log("err:", text);
        }
      });
  };

  const enableSubmit = () => {
    if (email === "" || password === "") {
      setValidTransaction(false);
    } else setValidTransaction(true);
  };

  const validate = (field, label) => {
    if (!field) {
      props.setStatus("Error: " + label);
      setTimeout(() => props.setStatus(""), 3000);
      return false;
    }
    return true;
  };

  const clearForm = () => {
    setEmail("");
    setPassword("");
    setValidTransaction(false);
  };

  return (
    <form>
      <label>Email address</label>
      <input
        type="input"
        className="form-control"
        id="email"
        placeholder="Enter email"
        value={email}
        onChange={(e) => {
          setEmail(e.currentTarget.value);
          enableSubmit();
        }}
      />
      <label>Password</label>
      <input
        type="password"
        className="form-control"
        id="password"
        placeholder="Enter password"
        autoComplete="current-password"
        value={password}
        onChange={(e) => {
          setPassword(e.currentTarget.value);
          enableSubmit();
        }}
      />
      <br />
      <button
        type="submit"
        className="form-control btn btn-light mb-1 mt-0"
        disabled={!validTransaction}
        onClick={handleLogin}
      >
        Submit
      </button>
    </form>
  );
};
export default Login;
